import React from "react";
import "./styles.scss";

const createMarkup = inputType => {
  const title = inputType.charAt(0).toUpperCase() + inputType.slice(1);

  return (
    <>
      <label
        htmlFor={inputType}
        style={{ display: "block", marginBottom: "4px" }}
      >
        {title}
      </label>
      <input
        className="co-input"
        type={inputType}
        id={inputType}
        style={{ marginBottom: "16px" }}
      />
    </>
  );
};

export const basic = () => {
  const types = ["password", "number", "email", "text"];
  let index = types.length - 1;
  let result = [];

  for (; index >= 0; index--) {
    result.push(createMarkup(types[index]));
  }

  return result;
};

