import React from "react";
import "./styles.scss";

const ListItemContent = n => <div style={{ padding: 8 }}>Item {n}</div>;

export const basic = () => {
  return (
    <ul className="co-list">
      <li className="co-list__item">{ListItemContent(1)}</li>
      <li className="co-list__item">{ListItemContent(2)}</li>
      <li className="co-list__item">{ListItemContent(3)}</li>
      <li className="co-list__item">{ListItemContent(4)}</li>
    </ul>
  );
};

export const divided = () => {
  return (
    <ul className="co-list co-list--divided">
      <li className="co-list__item">{ListItemContent(1)}</li>
      <li className="co-list__item">{ListItemContent(2)}</li>
      <li className="co-list__item">{ListItemContent(3)}</li>
      <li className="co-list__item">{ListItemContent(4)}</li>
    </ul>
  );
};

export const clickable = () => {
  return (
    <ul className="co-list co-list--divided">
      <li className="co-list__item">
        <a href="https://www.talkdesk.com" className="co-list__link">
          {ListItemContent("Selectable")}
        </a>
      </li>
      <li className="co-list__item">
        <a
          href="https://www.talkdesk.com"
          className="co-list__link"
          data-co-active
        >
          {ListItemContent("Selected")}
        </a>
      </li>
      <li className="co-list__item">
        <a
          href="https://www.talkdesk.com"
          className="co-list__link"
          data-co-focus
        >
          {ListItemContent("Focused")}
        </a>
      </li>
      <li className="co-list__item">
        <span className="co-list__link" data-co-disabled>
          {ListItemContent("Disabled")}
        </span>
      </li>
    </ul>
  );
};

export default { title: "List" };
