import React, { FC } from "react";
import styles from "@cobalt/css-calendar";
import { Weekday, Weekdays } from "./types";
import { getWeekDays } from "./utils";

export interface Props {
  days: Weekdays;
  weekStart: Weekday;
}

const WeekDays: FC<Props> = ({ days, weekStart }) => {
  return (
    <div className={styles["co-calendar__weekdays"]}>
      {getWeekDays(weekStart).map(weekday => {
        return (
          <div className={styles["co-calendar__weekday"]} key={weekday}>
            {days[weekday]}
          </div>
        );
      })}
    </div>
  );
};

export default WeekDays;
