export type CalendarType = HTMLDivElement;

export type Weekday = "MON" | "TUE" | "WED" | "THU" | "FRI" | "SAT" | "SUN";

export type Weekdays = {
  [k in Weekday]: string;
};

export interface DaysRange {
  start?: Day;
  end?: Day;
}

export type DaysType = Array<DaysRange | Day>;

export type Date = {
  year: number;
  month: number;
  day: number;
};

export type CallbackDate = (date: Date) => void;

export type RangeType = "first" | "between" | "last" | undefined;

