import React, { FunctionComponent, HTMLAttributes, Ref } from "react";
import cc from "classcat";
import styles from "@cobalt/css-divider";

export type DividerType = HTMLDivElement;

export interface Props extends HTMLAttributes<DividerType> {
  vertical?: boolean;
  forwardedRef?: Ref<DividerType>;
}

const Divider: FunctionComponent<Props> = ({
  className = "",
  forwardedRef,
  vertical,
  ...props
}) => {
  const classStyles = {
    [styles["co-divider"]]: true,
    [styles["co-divider--vertical"]]: vertical,
    [className]: !!className,
  };

  return (
    <div
      className={cc(classStyles)}
      ref={forwardedRef}
      role="separator"
      {...props}
    />
  );
};

export default Divider;
