import React, { FunctionComponent, ImgHTMLAttributes, Ref } from "react";
import cc from "classcat";
import styles from "@cobalt/css-image";

export type ImageType = HTMLImageElement;

export interface Props extends ImgHTMLAttributes<ImageType> {
  alt: string;
  src: string;
  forwardedRef?: Ref<ImageType>;
}

const Image: FunctionComponent<Props> = ({
  className = "",
  forwardedRef,
  alt,
  src,
  ...props
}) => {
  const classStyles = {
    [styles["co-image"]]: true,
    [className]: !!className
  };

  return (
    <img
      className={cc(classStyles)}
      ref={forwardedRef}
      src={src}
      alt={alt}
      {...props}
    />
  );
};

export default Image;
