import React, { FC, InputHTMLAttributes, Ref } from "react";
import cc from "classcat";
import styles from "@cobalt/css-color-input";
import { InputType } from "./InputTypes";

type InputAttributes = Omit<InputHTMLAttributes<InputType>, "type">;

export interface Props extends InputAttributes {
  forwardedRef?: Ref<InputType>;
}

const Color: FC<Props> = ({ className = "", forwardedRef, ...props }) => {
  const classStyles = cc([styles["co-color-input"], className]);

  return (
    <input type="color" className={classStyles} ref={forwardedRef} {...props} />
  );
};

export default Color;
