import React, { FunctionComponent, AnchorHTMLAttributes, Ref } from "react";
import cc from "classcat";
import styles from "@cobalt/css-link";

export type LinkType = HTMLAnchorElement;

export interface Props extends AnchorHTMLAttributes<LinkType> {
  href: string;
  forwardedRef?: Ref<LinkType>;
}

const Link: FunctionComponent<Props> = ({
  children,
  className = "",
  forwardedRef,
  href,
  rel,
  target,
  ...props
}) => {
  return (
    <a
      className={cc([styles["co-link"], className])}
      ref={forwardedRef}
      href={href}
      target={target}
      rel={!target ? rel : rel || "noreferrer noopener"}
      {...props}
    >
      {children}
    </a>
  );
};

export default Link;
