import React, { FC, Ref, HTMLAttributes } from "react";
import cc from "classcat";
import styles from "@cobalt/css-list";

export type ItemType = HTMLLIElement;

export interface Props extends HTMLAttributes<ItemType> {
  forwardedRef?: Ref<ItemType>;
}

const Item: FC<Props> = ({ className = "", forwardedRef, ...props }) => {
  const classes = cc([styles["co-list__item"], className]);

  return <li className={classes} ref={forwardedRef} {...props} />;
};

export default Item;
