import React, { FC, Ref, HTMLAttributes } from "react";
import cc from "classcat";
import styles from "@cobalt/css-list";

export type ListType = HTMLUListElement;

export interface Props extends HTMLAttributes<ListType> {
  divided?: boolean;
  forwardedRef?: Ref<ListType>;
}

const List: FC<Props> = ({
  className = "",
  forwardedRef,
  divided,
  ...props
}) => {
  const classStyles = cc([
    styles["co-list"],
    {
      [styles["co-list--divided"]]: divided
    },
    className
  ]);

  return <ul className={classStyles} ref={forwardedRef} {...props} />;
};

export default List;
