import React from "react";
import { render } from "@testing-library/react";
import Item, { ItemType } from "../Item";

describe("<Item />", () => {
  it("renders a Item", () => {
    const { container } = render(<Item>Item</Item>);

    expect(container).toMatchSnapshot();
  });

  it("renders with other attributes", () => {
    const { getByText } = render(<Item data-prop="test">Item</Item>);

    expect(getByText("Item").getAttribute("data-prop")).toEqual("test");
  });

  it("renders with default & custom classes", () => {
    const { getByText } = render(<Item className="customClassName">Item</Item>);

    const { classList } = getByText("Item");

    expect(classList.value).toBe("co-list__item customClassName");
  });

  it("renders with a ref", () => {
    const ref = React.createRef<ItemType>();

    const { getByTestId } = render(
      <Item forwardedRef={ref} data-testid="test">
        Item
      </Item>
    );

    expect(getByTestId("test")).toBe(ref.current);
  });
});
