import React from "react";
import { render } from "@testing-library/react";
import List, { ListType } from "../List";

describe("<List />", () => {
  it("renders a List", () => {
    const { container } = render(<List />);

    expect(container).toMatchSnapshot();
  });

  it("renders with other attributes", () => {
    const { getByTestId } = render(
      <List data-testid="list" data-prop="test" />
    );

    expect(getByTestId("list").getAttribute("data-prop")).toEqual("test");
  });

  it("renders with default & custom classes", () => {
    const { getByTestId } = render(
      <List data-testid="list-classname" className="customClassName" />
    );

    const { classList } = getByTestId("list-classname");

    expect(classList.value).toBe("co-list customClassName");
  });

  it("renders with a ref", () => {
    const ref = React.createRef<ListType>();

    const { getByTestId } = render(
      <List forwardedRef={ref} data-testid="test" />
    );

    expect(getByTestId("test")).toBe(ref.current);
  });

  it("renders divided", () => {
    const { getByTestId } = render(<List data-testid="list-divided" divided />);

    expect(getByTestId("list-divided").classList).toContain("co-list--divided");
  });
});
