import React, { FC, HTMLAttributes, Ref, createContext } from "react";

export type TContainerContext = "relative" | undefined;
export type ContainerType = HTMLDivElement;

const ContainerContext = createContext<TContainerContext>(undefined);

export interface Props extends HTMLAttributes<ContainerType> {
  forwardedRef?: Ref<ContainerType>;
}

const Container: FC<Props> = ({ forwardedRef, style, ...props }) => {
  return (
    <ContainerContext.Provider value="relative">
      <div
        ref={forwardedRef}
        style={{
          position: "relative",
          display: "inline-block",
          ...style
        }}
        {...props}
      />
    </ContainerContext.Provider>
  );
};

export { ContainerContext };
export default Container;
