import React from "react";
import { render } from "@testing-library/react";
import Container, { ContainerType } from "../Container";

describe("<Container />", () => {
  it("renders", () => {
    const { container } = render(<Container>Test</Container>);

    expect(container).toMatchSnapshot();
  });

  it("renders with other attributes", () => {
    const { getByText } = render(<Container data-prop="test">Test</Container>);

    expect(getByText("Test").getAttribute("data-prop")).toEqual("test");
  });

  it("renders with the default & custom styles", () => {
    const { getByText } = render(
      <Container style={{ color: "red" }}>Test</Container>
    );

    expect(getByText("Test").style.color).toBe("red");
  });

  it("renders with a ref", () => {
    const ref = React.createRef<ContainerType>();

    const { getByTestId } = render(
      <Container forwardedRef={ref} data-testid="test" />
    );

    expect(getByTestId("test")).toBe(ref.current);
  });
});
