import React, { FunctionComponent, HTMLAttributes, Ref } from "react";
import cc from "classcat";
import styles from "@cobalt/css-required";

export type RequiredType = HTMLElement;

export interface Props extends HTMLAttributes<RequiredType> {
  title: string;
  forwardedRef?: Ref<RequiredType>;
}

const Required: FunctionComponent<Props> = ({
  className = "",
  forwardedRef,
  title,
  ...props
}) => {
  return (
    <abbr
      className={cc([styles["co-required"], className])}
      ref={forwardedRef}
      title={title}
      {...props}
    >
      *
    </abbr>
  );
};

export default Required;
