import React, { FC, HTMLAttributes, Ref } from "react";
import cc from "classcat";
import styles from "@cobalt/css-table";

export type BodyType = HTMLTableSectionElement;

export interface Props extends HTMLAttributes<BodyType> {
  as?: "tbody" | "div";
  forwardedRef?: Ref<BodyType>;
}

const Body: FC<Props> = ({
  className = "",
  forwardedRef,
  as = "tbody",
  ...props
}) => {
  const classes = cc([styles["co-table__body"], className]);

  const Tag = as;

  return <Tag className={classes} ref={forwardedRef} {...props} />;
};

export default Body;
