import React, { FC, HTMLAttributes, Ref } from "react";
import cc from "classcat";
import styles from "@cobalt/css-table";

export type HeadType = HTMLTableSectionElement;

export interface Props extends HTMLAttributes<HeadType> {
  as?: "thead" | "div";
  forwardedRef?: Ref<HeadType>;
}

const Head: FC<Props> = ({
  className = "",
  forwardedRef,
  as = "thead",
  ...props
}) => {
  const classes = cc([styles["co-table__head"], className]);

  const Tag = as;

  return <Tag className={classes} ref={forwardedRef} {...props} />;
};

export default Head;
