import React from "react";
import { render } from "@testing-library/react";
import Body, { BodyType } from "../Body";

const renderOptions = {
  container: document.body.appendChild(document.createElement("table"))
};

describe("<Body />", () => {
  it("renders a table body", () => {
    const { container } = render(<Body />, renderOptions);

    expect(container).toMatchSnapshot();
  });

  it("renders a body with other attributes", () => {
    const { getByTestId } = render(
      <Body data-testid="test" data-prop="prop" />,
      renderOptions
    );

    expect(getByTestId("test").getAttribute("data-prop")).toEqual("prop");
  });

  it("renders a body with the default & custom class", () => {
    const { getByTestId } = render(
      <Body className="customClassName" data-testid="test" />,
      renderOptions
    );

    const { classList } = getByTestId("test");

    expect(classList.value).toBe("co-table__body customClassName");
  });

  it("renders with a ref", () => {
    const ref = React.createRef<BodyType>();

    const { getByTestId } = render(
      <Body forwardedRef={ref} data-testid="test" />,
      renderOptions
    );

    expect(getByTestId("test")).toBe(ref.current);
  });

  it("renders with a different tag", () => {
    const { getByTestId } = render(<Body as="div" data-testid="test" />);

    const node = getByTestId("test");

    expect(node.tagName).toBe("DIV");
  });
});
