import React from "react";
import { render } from "@testing-library/react";
import Head, { HeadType } from "../Head";

const renderOptions = {
  container: document.body.appendChild(document.createElement("table"))
};

describe("<Head />", () => {
  it("renders a table head", () => {
    const { container } = render(<Head />, renderOptions);

    expect(container).toMatchSnapshot();
  });

  it("renders a head with other attributes", () => {
    const { getByTestId } = render(
      <Head data-testid="test" data-prop="prop" />,
      renderOptions
    );

    expect(getByTestId("test").getAttribute("data-prop")).toEqual("prop");
  });

  it("renders a head with the default & custom class", () => {
    const { getByTestId } = render(
      <Head className="customClassName" data-testid="test" />,
      renderOptions
    );

    const { classList } = getByTestId("test");

    expect(classList.value).toBe("co-table__head customClassName");
  });

  it("renders with a ref", () => {
    const ref = React.createRef<HeadType>();

    const { getByTestId } = render(
      <Head forwardedRef={ref} data-testid="test" />,
      renderOptions
    );

    expect(getByTestId("test")).toBe(ref.current);
  });

  it("renders with a different tag", () => {
    const { getByTestId } = render(<Head as="div" data-testid="test" />);

    const node = getByTestId("test");

    expect(node.tagName).toBe("DIV");
  });
});
