import React from "react";
import { render } from "@testing-library/react";
import HeadCell, { HeadCellType } from "../HeadCell";

const renderOptions = {
  container: document.body.appendChild(document.createElement("tr"))
};

describe("<HeadCell />", () => {
  it("renders a table headcell", () => {
    const { container } = render(<HeadCell />, renderOptions);

    expect(container).toMatchSnapshot();
  });

  it("renders a headcell with other attributes", () => {
    const { getByTestId } = render(
      <HeadCell data-testid="test" data-prop="prop" />,
      renderOptions
    );

    expect(getByTestId("test").getAttribute("data-prop")).toEqual("prop");
  });

  it("renders a headcell with the default & custom class", () => {
    const { getByTestId } = render(
      <HeadCell className="customClassName" data-testid="test" />,
      renderOptions
    );

    const { classList } = getByTestId("test");

    expect(classList.value).toBe("co-table__cell customClassName");
  });

  it("renders with a ref", () => {
    const ref = React.createRef<HeadCellType>();

    const { getByTestId } = render(
      <HeadCell forwardedRef={ref} data-testid="test" />,
      renderOptions
    );

    expect(getByTestId("test")).toBe(ref.current);
  });

  it("renders with a different tag", () => {
    const { getByTestId } = render(<HeadCell as="div" data-testid="test" />);

    const node = getByTestId("test");

    expect(node.tagName).toBe("DIV");
  });

  it("renders a headcell with a defined width", () => {
    const { getByTestId } = render(
      <HeadCell data-testid="test" width="33" />,
      renderOptions
    );

    expect(getByTestId("test").classList).toContain("co-table__cell--33");
  });
});
